    //     const arrangeitems = Object.assign([], this.state.arrangeitems);
    //     const droppedUser = this.state.arrangeitems[source.index];


    //     arrangeitems.splice(source.index, 1);
    //     arrangeitems.splice(destination.index, 0, droppedUser);
    //     this.setState({
    //         arrangeitems
    //     });
    //   }
    //  handleDragStart(e) {
    //     this.style.opacity = '0.4';

    //     dragSrcEl = this;

    //     e.dataTransfer.effectAllowed = 'move';
    //     e.dataTransfer.setData('text/html', this.innerHTML);
    //   }
    //    handleDrop(e) {
    //     e.stopPropagation();

    //       if (dragSrcEl !== this) {
    //         dragSrcEl.innerHTML = this.innerHTML;
    //         this.innerHTML = e.dataTransfer.getData('text/html');
    //       }

    //       return false;
    //     }
render(){
    const { items } = this.state.arrangeitems
    console.log(items);

    if (items) {
               // console.log(items);

                const listItems=items.map(item => {
                    return (
                        // <DragDropContext onDragEnd={this.onDragEnd}>
                        // <Droppable droppableId={item.key}>
                        //     { provided=>(

                                <div 
                                 className="list" key={item.key}>
                                 <p draggable="true">
                                 <i class="fas fa-arrows-alt"></i>
                                 {item.text}
                                 </p>
                                 </div>


                            //      )

                            // }

                        //  </Droppable>
                        //  </DragDropContext>
                    )
                })
                return(
                    <div>{listItems}</div>
                )


            }
    return(
        <>


